import { useParams } from "react-router"
import { Route } from "react-router"
import Comments from '../comments/Comments'

const QuoteDetail = ()=>{
    const params=  useParams()
    return(
        <div>
        <h1>Single Quote</h1>
        <p>{params.id}</p>
        <Route path={`/quotes/${params.id}/comments`}>
            <Comments/>

        </Route>

        </div>
    )
}
export default QuoteDetail
