import QuoteForm from '../quotes/QuoteForm'
import { useHistory } from 'react-router'
import { useEffect, useState } from 'react';

const AddQuote = ()=>{
    let status;
    const history = useHistory()
    const[error,setError] = useState(null)
    // useEffect(()=>{
    //     if(status==="completed"){
    //         history.push("/quotes")
    //     }
    // },[status,history])
    const addingQuote = (quote)=>{
        console.log(quote)
        const sendingData = async()=>{
        const response = await fetch("https://webapp-e180b-default-rtdb.firebaseio.com/quotes.json",{
            method:"POST",
            body:JSON.stringify({author:quote.author,text:quote.text})
        })
    }
        try{
            sendingData()
            history.push("/quotes")
            
        }
        catch(err){
            // throw new Error ("Something went wrong!");
            setError("Error")
        }
        

    }
    return(
        (error === null ? <p>{error} </p> : <QuoteForm onAdding = {addingQuote}/>  )
    )
}

export default AddQuote;
